import React from 'react'
import Starbucks from './Starbucks'
import FastFood from './FastFood'

class Main extends React.Component {

    state={
        starBucksDollars: 0,
        starBucksDays: 0
    }

    starbucksDollarSavedClickHandler = (e) => {
        let starBucksDollars = this.state.starBucksDollars;
        let starBucksDays = this.state.starBucksDays;
        if(e.currentTarget.innerText === "+"){
            starBucksDollars += 6
            starBucksDays += 1
            this.setState({
                starBucksDollars:starBucksDollars,
                starBucksDays:starBucksDays
            })
        }else{
            starBucksDollars -= 6
            starBucksDays += 1
            this.setState({
                starBucksDollars:starBucksDollars,
                starBucksDays:starBucksDays
            })
        }
    }

    render() {


        return (
            <div>
                <div>
                    <Starbucks
                        starBucksDollars={this.state.starBucksDollars}
                        starBucksDays={this.state.starBucksDays}
                        starbucksClick={this.starbucksDollarSavedClickHandler}
                    />
                </div>
                <div>
                    <FastFood
                    />
                </div>
            </div>
        )
    }
}

export default Main;
